const createSpy = require("../../../helpers/spy");

const capitalizeFirst = string => string.substr(0, 1).toUpperCase() + string.substr(1);

const Voice = { // eslint-disable-line object-shorthand
    trigger(event, data) {
        const functionName = "onSpeech" + capitalizeFirst(event);
        if (this[functionName]) {
            this[functionName](data);
        }
    },
    start: createSpy(),
    stop: createSpy(),
    cancel: createSpy(),
    reset() {
        this.start.reset();
        this.stop.reset();
        this.cancel.reset();
    },
    mockedIsAvailable: false,
    mockedIsRecognizing: false,
    isAvailable() {
        return this.mockedIsAvailable;
    },
    isRecognizing() {
        return this.mockedIsRecognizing;
    },
};

module.exports = Voice;
